import { Command } from ".";
export const isPercentage = (value) => {
    return value === Command.Percentage;
};
export const isSign = (value) => {
    return value === Command.Sign;
};
export const isEqual = (value) => {
    return value === Command.Equal;
};
export const isClear = (value) => {
    return value === Command.Clear;
};
export const isAllClear = (value) => {
    return value === Command.AllClear;
};
export const isCommand = (value) => {
    return Object.values(Command).includes(value);
};
export const isBasicOperator = (value) => {
    return (value === Command.Addition ||
        value === Command.Subtraction ||
        value === Command.Division ||
        value === Command.Multiplication);
};
